INSERT INTO `codes` VALUES (77,'protocol','class Protocol\r\n  \r\n  # io\r\n  CELLS = \"Comp Cells\"\r\n  INPUT = \"Plasmid\"\r\n  OUTPUT = \"Transformed E Coli\"\r\n  \r\n  # debug\r\n  DEBUG_WITH_REPLACEMENT = true\r\n  \r\n  # specs\r\n  RESUSPENSION_VOL = 900 # how much to resuspend transformed cells in\r\n    \r\n  def main\r\n    # Detract comp cells from batches, store how many of each type of comp cell there are, and figure out how many Amp vs Kan plates will be needed \r\n    \r\n    # Determine replacements of e coli comp cell batch\r\n    determine_replacements\r\n\r\n    # Detract from running batches\r\n    operations.running.each { |op| op.input(CELLS).collection.remove_one op.input(CELLS).sample }\r\n\r\n    # Exit early if there are no more running operations\r\n    if operations.empty?\r\n      show do\r\n        title \"All operations have errored\"\r\n        note \"All operations have errored out.\"\r\n      end\r\n      return {}\r\n    end\r\n  \r\n    # Make \r\n    operations.running.retrieve(only: [\"Plasmid\"]).make\r\n   \r\n    # Prepare electroporator \r\n    prepare_electroporator\r\n      \r\n    # Measure plasmid stock concentrations\r\n    ops_for_dilution = operations.running.select { |op| op.input(INPUT).object_type.name == \"Plasmid Stock\" }\r\n    ops_for_measurement = ops_for_dilution.select { |op| op.input(INPUT).item.get(:concentration).to_f == 0.0 }\r\n    measure_plasmid_stock ops_for_measurement\r\n\r\n    # Dilute plasmid stocks\r\n    dilute_plasmid_stocks ops_for_dilution\r\n  \r\n    # Get comp cells and cuvettes \r\n    get_cold_items  \r\n      \r\n    # Label aliquots\r\n    label_aliquots  \r\n      \r\n    index = 0\r\n  \r\n    # Display table to tech\r\n    display_table index\r\n      \r\n    #plate pre heating\r\n    plate_preheating  \r\n    \r\n    # Incubate transformants\r\n    incubate_transformants \r\n\r\n    # Clean up\r\n    clean_up\r\n      \r\n    # Move items\r\n      operations.running.each do |op|\r\n        op.output(OUTPUT).item.move \"37C shaker\"\r\n      end\r\n      \r\n    # Store dna stocks\r\n      all_stocks = operations.running.map { |op| [op.input(INPUT).item, op.temporary[:old_stock]] }.flatten.uniq\r\n      all_stocks.compact!\r\n      release all_stocks, interactive: true, method: \"boxes\"\r\n      \r\n    return {}\r\n  end\r\n  \r\n  def prepare_electroporator\r\n    show do\r\n      title \"Prepare bench\"\r\n      note \"If the electroporator is off (no numbers displayed), turn it on using the ON/STDBY button.\"\r\n      note \"Set the voltage to 1250V by clicking the up and down buttons.\"\r\n      note \" Click the time constant button to show 0.0.\"\r\n      image \"Actions/Transformation/initialize_electroporator.jpg\"\r\n      check \"Retrieve and label #{operations.running.length} 1.5 mL tubes with the following ids: #{operations.running.collect { |op| \"#{op.output(OUTPUT).item.id}\"}.join(\",\")} \"\r\n      check \"Set your 3 pipettors to be 2 uL, 42 uL, and 900 uL.\"\r\n      check \"Prepare 10 uL, 100 uL, and 1000 uL pipette tips.\"      \r\n      check \"Grab a Bench SOC liquid aliquot (sterile) and loosen the cap.\"\r\n    end\r\n  end\r\n  \r\n  def measure_plasmid_stock ops_for_measurement\r\n    if ops_for_measurement.any?\r\n      conc_table = Proc.new { |ops|\r\n        ops.start_table\r\n          .input_item(INPUT)\r\n          .custom_input(:concentration, heading: \"Concentration (ng/ul)\", type: \"number\") { |op| \r\n            x = op.temporary[:concentration] || -1\r\n            x = rand(10..100) if debug\r\n            x\r\n          }\r\n          .validate(:concentration) { |op, v| v.between?(0,10000) }\r\n          .validation_message(:concentration) { |op, k, v| \"Concentration must be non-zero!\" }\r\n          .end_table.all\r\n      }\r\n      \r\n      show_with_input_table(ops_for_measurement, conc_table) do\r\n        title \"Measure concentrations\"\r\n        note \"The concentrations of some plasmid stocks are unknown.\"\r\n        check \"Go to the nanodrop and measure the concentrations for the following items.\"\r\n        check \"Write the concentration on the side of each tube\"\r\n      end\r\n      \r\n      ops_for_measurement.each do |op|\r\n        op.input(INPUT).item.associate :concentration, op.temporary[:concentration]\r\n      end\r\n    end\r\n  end\r\n  \r\n  def get_cold_items  \r\n    show do \r\n      title \"Get cold items\"\r\n      note \"Retrieve a styrofoam ice block and an aluminum tube rack. Put the aluminum tube rack on top of the ice block.\"\r\n      image \"arrange_cold_block\"\r\n      check \"Retrieve #{operations.length} cuvettes and put inside the styrofoam touching ice block.\"\r\n      note \"Retrieve the following electrocompetent aliquots from the M80 and place them on an aluminum tube rack: \"\r\n      operations.group_by { |op| op.input(CELLS).item }.each do |batch, grouped_ops|\r\n        check \"#{grouped_ops.size} aliquot(s) of #{grouped_ops.first.input(CELLS).sample.name} from batch #{batch.id}\"\r\n      end\r\n      image \"Actions/Transformation/handle_electrocompetent_cells.jpg\"\r\n    end\r\n  end\r\n  \r\n  def label_aliquots  \r\n    show do \r\n      title \"Label aliquots\"\r\n      aliquotsLabeled = 0\r\n      operations.group_by { |op| op.input(CELLS).item }.each do |batch, grouped_ops|\r\n        if grouped_ops.size == 1\r\n          check \"Label the electrocompetent aliquot of #{grouped_ops.first.input(CELLS).sample.name} as #{aliquotsLabeled + 1}.\"\r\n        else\r\n          check \"Label each electrocompetent aliquot of #{grouped_ops.first.input(CELLS).sample.name} from #{aliquotsLabeled + 1}-#{grouped_ops.size + aliquotsLabeled}.\"\r\n        end\r\n        aliquotsLabeled += grouped_ops.size\r\n      end\r\n      note \"If still frozen, wait till the cells have thawed to a slushy consistency.\"\r\n      warning \"Transformation efficiency depends on keeping electrocompetent cells ice-cold until electroporation.\"\r\n      warning \"Do not wait too long\"\r\n      image \"Actions/Transformation/thawed_electrocompotent_cells.jpg\"\r\n    end\r\n  end\r\n  \r\n  def display_table index\r\n    show do\r\n      title \"Add plasmid to electrocompetent aliquot, electroporate and rescue \"\r\n      note \"Repeat for each row in the table:\"\r\n      check \"Pipette 2 uL plasmid/gibson result into labeled electrocompetent aliquot, swirl the tip to mix and place back on the aluminum rack after mixing.\"\r\n      check \"Transfer 42 uL of e-comp cells to electrocuvette with P100\"\r\n      check \"Slide into electroporator, press PULSE button twice, and QUICKLY add #{RESUSPENSION_VOL} uL of SOC\"\r\n      check \"pipette cells up and down 3 times, then transfer #{RESUSPENSION_VOL} uL to appropriate 1.5 mL tube with P1000\"\r\n      table operations.running.start_table \r\n        .input_item(\"Plasmid\")\r\n        .custom_column(heading: \"Electrocompetent Aliquot\") { index = index + 1 }\r\n        .output_item(\"Transformed E Coli\", checkable: true)\r\n        .end_table\r\n    end\r\n  end\r\n  \r\n  def incubate_transformants \r\n    show do \r\n      title \"Incubate transformants\"\r\n      check \"Grab a glass flask\"\r\n      check \"Place E. coli transformants inside flask laying sideways and place flask into shaking 37 C incubator.\"\r\n      #Open google timer in new window\r\n      note \"Transformants with an AMP marker should incubate for only 30 minutes. Transformants with a KAN, SPEC, or CHLOR marker needs to incubate for 60 minutes.\"\r\n      note \"<a href=\\\'https://www.google.com/search?q=30%20minute%20timer\\\' target=\\\'_blank\\\'>Use a 30 minute Google timer</a> or <a href=\\\'https://www.google.com/search?q=60%20minute%20timer\\\' target=\\\'_blank\\\'>a 60 minute Google timer</a> to set a reminder to retrieve the transformants, at which point you will start the \\\'Plate Transformed Cells\\\' protocol.\"\r\n      image \"Actions/Transformation/37_c_shaker_incubator.jpg\"\r\n      note \"While the transformants incubate, finish this protocol by completing the remaining tasks.\"\r\n    end\r\n  end\r\n  \r\n  def plate_preheating  \r\n    show do \r\n      title \"Pre-heat plates\"\r\n      note \"Retrieve the following plates, and place into still 37C incubator.\"    \r\n      grouped_by_marker = operations.running.group_by { |op|\r\n        op.input(INPUT).sample.properties[\"Bacterial Marker\"].upcase\r\n      }\r\n      grouped_by_marker.each do |marker, ops|\r\n        check \"#{ops.size} LB + #{marker} plates\"\r\n      end\r\n      image \"Actions/Plating/put_plate_incubator.JPG\"\r\n    end\r\n  end\r\n  \r\n  def clean_up\r\n    show do\r\n      title \"Clean up\"\r\n      check \"Put all cuvettes into biohazardous waste.\"\r\n      check \"Discard empty electrocompetent aliquot tubes into waste bin.\"\r\n      check \"Return the styrofoam ice block and the aluminum tube rack.\"\r\n      image \"Actions/Transformation/dump_dirty_cuvettes.jpg\"\r\n    end\r\n  end\r\n  \r\n  def determine_replacements\r\n    operations.running.each do |op|\r\n      # If current batch is empty\r\n      if op.input(CELLS).collection.empty? || (debug and DEBUG_WITH_REPLACEMENT)\r\n        old_batch = op.input(CELLS).collection\r\n        \r\n        # Find replacement batches\r\n        all_batches = Collection.where(object_type_id: old_batch.object_type.id).select { |b| !b.empty? && !b.deleted? && (b.matrix[0].include? op.input(CELLS).sample.id) }\r\n        # batches_of_cells = all_batches.select { |b| b.include? op.input(CELLS).sample && !b.deleted? }.sort { |x| x.num_samples }\r\n        batches_of_cells = all_batches.reject { |b| b == old_batch }.sort { |x| x.num_samples } # debug specific rejection to force replacement\r\n        \r\n        # Error if not enough\r\n        if batches_of_cells.empty?\r\n          op.error :not_enough_comp_cells, \"There were not enough comp cells of #{op.input(CELLS).sample.name} to complete the operation.\"\r\n        else\r\n          # Set input to new batch\r\n          \r\n          op.input(CELLS).set collection: batches_of_cells.last\r\n          # Display warning\r\n          op.associate :comp_cell_batch_replaced, \"There were not enough comp cells for this operation. Replaced batch #{old_batch.id} with batch #{op.input(CELLS).collection.id}\"\r\n        end\r\n      end\r\n    end\r\n  end\r\n  \r\n  def dilute_plasmid_stocks ops_for_dilution\r\n    if ops_for_dilution.any?\r\n      show do\r\n        title \"Prepare plasmid stocks\"\r\n        \r\n        ops_for_dilution.each do |op|\r\n          i = produce new_sample op.input(INPUT).sample.name, of: op.input(INPUT).sample_type, as: \"1 ng/L Plasmid Stock\"\r\n          \r\n          op.temporary[:old_stock] = op.input(INPUT).item\r\n          op.input(INPUT).item.associate :from, op.temporary[:old_stock].id\r\n          vol = 0.5\r\n          c = op.temporary[:old_stock].get(:concentration).to_f\r\n          op.temporary[:water_vol] = (vol * c).round(1)\r\n          op.temporary[:vol] = vol\r\n          op.input(INPUT).set item: i\r\n          op.associate :plasmid_stock_diluted, \"Plasmid stock #{op.temporary[:old_stock].id} was diluted and a 1 ng/ul Plasmid Stock was created: #{op.input(INPUT).item.id}\"\r\n        end\r\n        \r\n        check \"Grab <b>#{ops_for_dilution.size}</b> 1.5 mL tubes and place in rack\"\r\n        note \"According to the table below:\"\r\n        check \"Label all tubes with the corresponding Tube id\"\r\n        check \"Pipette MG H20\"\r\n        check \"Pipette DNA\"\r\n        table ops_for_dilution.start_table\r\n          .input_item(INPUT, heading: \"Tube id\", checkable: true)\r\n          .custom_column(heading: \"MG H20\", checkable: true) { |op| \"#{op.temporary[:water_vol]} ul\" }\r\n          .custom_column(heading: \"Plasmid Stock (ul)\", checkable: true) { |op| \"#{op.temporary[:vol]} ul of #{op.temporary[:old_stock].id}\" }\r\n          .end_table\r\n      end\r\n      \r\n      show do\r\n        title \"Set aside old plasmid stocks\"\r\n        \r\n        note \"The following plasmid stocks will no longer be needed for this protocol.\"\r\n        check \"Set aside the old plasmid stocks:\"\r\n        ops_for_dilution.each do |op|\r\n          check \"#{op.temporary[:old_stock]}\"\r\n        end\r\n      end\r\n    end\r\n  end\r\nend ',7,'OperationType','2018-07-17 20:59:34','2018-07-17 20:59:34',1),(102,'protocol','class Protocol\n\n  def main\n    # Take plates  \n    operations.retrieve\n    \n    # Count the number of colonies\n    info = get_colony_numbers\n    \n    # Update plate data\n    update_item_data info\n    \n    # Delete and discard any plates that have 0 colonies\n    discard_bad_plates if operations.any? { |op| op.temporary[:delete] }\n    \n    # Parafilm and label plates \n    parafilm_plates\n    \n    # Return plates\n    operations.store\n    \n    return {}\n  end\n  \n  \n  # Count the number of colonies and select whether the growth is normal, contaminated, or a lawn\n  def get_colony_numbers\n    show do\n      title \"Estimate colony numbers\"\n      \n      operations.each do |op|\n        plate = op.input(\"Plate\").item\n        get \"number\", var: \"n#{plate.id}\", label: \"Estimate how many colonies are on #{plate}\", default: 5\n        select [\"normal\", \"contamination\", \"lawn\"], var: \"s#{plate}\", label: \"Choose whether there is contamination, a lawn, or whether it\'s normal.\"\n      end\n    end    \n  end\n  \n  # Alter data of the virtual item to represent its actual state\n  def update_item_data info\n    operations.each do |op|\n      plate = op.input(\"Plate\").item\n      if info[\"n#{plate.id}\".to_sym] == 0\n        plate.mark_as_deleted\n        plate.save\n        op.temporary[:delete] = true\n        op.error :no_colonies, \"There are no colonies for plate #{plate.id}\"\n      else\n        plate.associate :num_colonies, info[\"n#{plate.id}\".to_sym]\n        plate.associate :status, info[\"s#{plate.id}\".to_sym]\n        \n        checked_ot = ObjectType.find_by_name(\"Checked E coli Plate of Plasmid\")\n        plate.store if plate.object_type_id != checked_ot.id\n        plate.object_type_id = checked_ot.id\n        plate.save\n        op.output(\"Plate\").set item: plate\n        \n        op.plan.associate \"plate_#{op.input(\"Plate\").sample.id}\", plate.id\n      end\n    end\n  end\n  \n  # discard any plates that have 0 colonies\n  def discard_bad_plates\n      show do \n        title \"Discard Plates\"\n        \n        discard_plate_ids = operations.select { |op| op.temporary[:delete] }.map { |op| op.input(\"Plate\").item.id }\n        note \"Discard the following plates with 0 colonies: #{discard_plate_ids}\"\n      end\n  end\n  \n  # Parafilm and label any plates that have suitable growth\n  def parafilm_plates\n    show do \n      title \"Label and Parafilm\"\n      \n      plates_to_parafilm = operations.reject { |op| op.temporary[:delete] }.map { |op| op.input(\"Plate\").item.id }\n      note \"Perform the steps with the following plates: #{plates_to_parafilm}\"\n      note \"Label the plates with their item ID numbers on the side, and parafilm each one.\"\n      note \"Labelling the plates on the side makes it easier to retrieve them from the fridge.\"\n    end\n  end\nend',9,'OperationType','2018-07-17 21:17:49','2018-07-17 21:17:49',1),(116,'protocol','needs \"Cloning/StandardCloning\"\n\nclass Protocol\n\n  include StandardCloning\n  \n  def main\n    operations.retrieve.make\n    \n    # Print out labels\n    print_labels\n\n    # Pipette glycerol into cryo tubes\n    pipette_glycerol\n\n    # Transfer into cryo tubes\n    transfer_into_cryo_tubes\n\n    # Sequencing results\n    sequencing_results\n\n    # Discard overnights  \n    discard_overnights\n\n    operations.store\n    \n    return {}\n  end\n  \n  \n  def print_labels\n    show do\n        title \"Print out labels\"\n        \n        note \"On the computer near the label printer, open Excel document titled \'Glycerol stock label template\'.\" \n        note \"Copy and paste the table below to the document and save.\"\n        \n        table operations.start_table \n            .output_item(\"Stock\") \n            .custom_column(heading: \"Sample ID\") { |op| op.output(\"Stock\").sample.id } \n            .custom_column(heading: \"Sample Name\") { |op| op.output(\"Stock\").sample.name[0,16] }\n        .end_table\n\n        note \"Ensure that the correct label type is loaded in the printer: B33-181-492 should show up on the display. \n          If not, get help from a lab manager to load the correct label type.\"\n        note \"Open the LabelMark 6 software and select \'Open\' --> \'File\' --> \'Glycerol stocks.l6f\'\"\n        note \"A window should pop up. Under  \'Start\' enter #{operations.first.output(\"Stock\").item.id} and set \'Total\' to #{operations.length}. Select \'Finish.\'\"\n        note \"Click on the number in the top row of the horizontal side label and select \'Edit External Data\'. A window should pop up. Select \'Finish\'.\"\n        note \"Select \'File\' --> \'Print\' and set the printer to \'BBP33\'.\"\n        note \"Collect labels.\"\n    end\n  end\n  \n  def pipette_glycerol\n    show do \n        title \"Pipette Glycerol into Cryo Tubes\"\n        \n        check \"Take #{operations.length} Cryo #{\"tube\".pluralize(operations.length)}\"\n        check \"Label each tube with the printed out labels\"\n        check \"Pipette 900 uL of 50 percent Glycerol into each tube.\"\n        warning \"Make sure not to touch the inner side of the Glycerol bottle with the pipetter.\"\n    end\n  end\n  \n  \n  def transfer_into_cryo_tubes\n    show do \n        title \"Transfer Into Cryo Tubes\"\n        \n        note \"Transfer <b>900 uL</b> of culture according to the following table:\"\n        \n        table operations.start_table\n            .custom_column(heading: \"Overnight\") { |op| op.input(\"Overnight\").item.id } \n            .custom_column(heading: \"Glycerol Stock ID\", checkable: true) { |op| op.output(\"Stock\").item.id }  \n        .end_table\n        \n        note \"Cap the Cryo tube and then vortex on a table top vortexer for about 20 seconds.\"\n    end\n  end\n  \n  def sequencing_results\n    operations.each do |op|\n        on = op.input(\"Overnight\").item\n        gs = op.output(\"Stock\").item\n        \n        pass_data \"sequencing results\", \"sequence_verified\", from: on, to: gs\n        \n        on.mark_as_deleted\n        on.save\n    end\n  end\n  \n  def discard_overnights\n    show do \n        title \"Discard overnights\"\n        \n        note \"Please discard all of the following overnights in the dishwashing area:\"\n        note operations.map { |op| op.input(\"Overnight\").item.id }.to_sentence\n    end\n  end\nend\n',15,'OperationType','2018-07-17 21:25:30','2018-07-17 21:25:30',1),(117,'protocol','class Protocol\n    \n  def main\n    # debuggin\'\n    if debug\n        operations.each do |op| \n            op.plan.associate \"Item #{op.input(\"Stock\").item.id} sequencing ok?\", [\"yes somestuff\", \"no foo\", \"resequence bar\"].sample\n        end\n    end\n\n    operations.retrieve interactive: false \n    \n    # Gather user responses\n    gather_user_responses\n\n    # Discard plates for yes\n    discard_plates_good\n\n    # Discard plasmid stocks for no\n    discard_plates_bad\n\n    return {}\n  end\n  \n\n  def gather_user_responses\n    operations.select { |op| op.plan.get(\"Item #{op.input(\"Stock\").item.id} sequencing ok?\") }.each do |op|\n        ans = op.plan.get(\"Item #{op.input(\"Stock\").item.id} sequencing ok?\").downcase\n        if ans.include? \"yes\"\n            op.plan.associate \"seq_notice_#{op.input(\"Stock\").item.id}\".to_sym, \"Plate #{op.input(\"Plate\").item.id} has been discarded.\"\n            \n            op.temporary[:yes] = true\n        elsif ans.include? \"resequence\"\n            op.plan.associate \"seq_notice_#{op.input(\"Stock\").item.id}\".to_sym, \"Plasmid stock not verified; please resubmit this stock for sequencing.\"\n            \n            op.temporary[:resequence] = true\n        else\n            op.plan.associate \"seq_notice_#{op.input(\"Stock\").item.id}\".to_sym, \"Plasmid stock #{op.input(\"Stock\").item.id} has been discarded.\"\n            \n            op.temporary[:no] = true\n        end\n    end\n  end\n  \n  def discard_plates_good\n    show do \n        title \"Discard plates from good sequencing results\"\n        \n        note \"Please discard the following plates: \"\n        operations.select { |op| op.temporary[:yes] }.each do |op|\n            pl = op.input(\"Plate\").item\n            note \"Plate #{pl.id} at #{pl.location}\"\n            pl.mark_as_deleted\n            pl.save\n        end\n    end if operations.any? { |op| op.temporary[:yes] }\n  end\n  \n  def discard_plates_bad\n    show do\n        title \"Discard Plasmid Stocks from bad sequencing results\"\n        \n        note \"Please discard the following Plasmid Stocks:\"\n        operations.select { |op| op.temporary[:no] }.each do |op|\n            stock = op.input(\"Stock\").item\n            note \"Plasmid Stock #{stock.id} at #{stock.location}\"\n            stock.mark_as_deleted\n            stock.save\n        end\n    end if operations.any? { |op| op.temporary[:no] }\n  end\nend\n',14,'OperationType','2018-07-17 21:26:00','2018-07-17 21:26:00',1),(118,'protocol','needs \"Cloning/StandardCloning\"\n\nclass Protocol\n\n  include StandardCloning\n\n  def main\n    # Find all overnights and take them\n        operations.retrieve\n\n    # Verify whether each overnight has growth\n        verify_growth = show do\n            title \"Check if overnights have growth\"\n            note \"Choose No for the overnight that does not have growth and throw them away or put in the clean station.\"\n            operations.each do |op|\n                item_id = op.input(\"Plasmid\").child_item.id\n                select [\"Yes\", \"No\"], var: \"#{item_id}\", label: \"Does tube #{item_id} have growth?\"\n            end\n        end\n        \n    # if no growth, delete the overnight    \n        operations.each do |op|\n            item = op.input(\"Plasmid\").child_item\n            if verify_growth[\"#{item.id}\".to_sym] == \"No\"\n                item.mark_as_deleted\n                op.error :no_growth, \"The overnight has no growth.\" \n            end\n        end\n        \n    operations.running.make\n    \n    #transfer each overnight into 1.5 mL tube\n        show do \n            title \"Transfer Overnights into 1.5 mL Tubes\"\n            note \"Grab #{operations.length} 1.5 mL tubes and label from 1 to #{operations.length}\"\n            note \"Transfer 1.5 mL of the overnight into the corresponding 1.5 mL tube.\"\n            index = 0\n            table operations.start_table\n                .input_item(\"Plasmid\")\n                .custom_column(heading: \"Tube Number\") { index = index + 1 }\n            .end_table\n        end\n        \n    #Spin down cells and remove supernatant\n        show do \n           title \"Spin down the cells\"\n           check \"Spin at 5,800 xg for 2 minutes, make sure to balance.\"\n           check \"Remove the supernatant. Pour off the supernatant into liquid waste, being sure not to upset the pellet. Pipette out the residual supernatant.\"\n        end\n        \n    # Resuspend in P1, P2, N3\n        show do\n          title \"Resuspend in P1, P2, N3\"\n          check \"Add 250 uL of P1 into each tube and vortex strongly to resuspend.\"\n          check \"Add 250 uL of P2 and gently invert 5-10 times to mix, tube contents should turn blue.\"\n          check \"Pipette 350 uL of N3 into each tube and gently invert 5-10 times to mix. Tube contents should turn colorless.\"\n     