arning \"For this step, use a new pipette tip for each sample to avoid cross contamination\"\n            check \"Pipette 50 uL of water into the CENTER of each column\"\n            check \"Let the tubes sit on the bench for 2 minutes\"\n            check \"Spin the columns at 17,000 xg for 1 minute\"\n            check \"Remove the tubes and discard the columns\"    \n        end \n        \n    # Relabel tubes w output ids\n        show do \n            title \"Relabel Tubes\"\n            note \"Relabel each tube with the corresponding item ID\"\n            index = 0\n            table operations.start_table \n                .custom_column(heading: \"Tube Number\") { index = index + 1 }\n                .output_item(\"Plasmid\")\n            .end_table\n        end\n    \n    # nanodrop and get concentration\n        show do \n            title \"Nanodrop and Enter Concentration\"\n            note \"Nanodrop each plasmid and enter the concentration below\"\n            table operations.start_table\n                .output_item(\"Plasmid\")\n                .get(:concentration, type: \"number\", heading: \"Concentration\", default: 200)\n            .end_table\n        end\n        \n    # set concentration of plasmid stock and change location of overnights\n        operations.running.each do | op |\n            op.set_output_data \"Plasmid\", :concentration, op.temporary[:concentration]\n            op.set_output_data \"Plasmid\", :from, op.input(\"Plasmid\").item.id\n            op.plan.associate \"overnight_#{op.input(\"Plasmid\").sample.id}\", op.input(\"Plasmid\").item.id\n            op.plan.associate :plasmid, op.output(\"Plasmid\").item.id\n            op.input(\"Plasmid\").child_item.store\n                    \n            pass_data \"sequencing results\", \"sequence_verified\", from: op.input(\"Plasmid\").item, to: op.output(\"Plasmid\").item\n        end\n         \n        operations.running.store\n        return {}\n  end\n\nend\n',11,'OperationType','2018-07-17 21:26:43','2018-07-17 21:26:43',1),(122,'protocol','needs \"Standard Libs/UploadHelper\"\nclass Protocol\n    \n    include UploadHelper\n        \n    # I/O\n    FRAGMENT=\"Fragment\"\n    FRAGMENT_OUT=\"Fragment\"\n    \n    # upload stuff\n    DIRNAME=\"<where are gel files on computer>\"\n    TRIES=3\n    \n    # gel stuff\n    MIN_WEIGHT = 0.0\n    MAX_WEIGHT = 10.0\n    CORRECT=[\"y\",\"n\"] # values for debug\n\n    def main\n        # Sort operations by gels and columns (these can get out of order from PCR)\n        operations.sort! do |op1, op2| \n            fv1 = op1.input(FRAGMENT)\n            fv2 = op2.input(FRAGMENT)\n            [fv1.item.id, fv1.row, fv1.column] <=> [fv2.item.id, fv2.row, fv2.column]\n        end\n        \n        operations.retrieve(interactive: false)\n        \n        # get gel images\n        gels = operations.map { |op| op.input(FRAGMENT).collection}.uniq\n        gels.each { |gel|\n        \n            grouped_ops=operations.select { |op| op.input(FRAGMENT).collection == gel }\n            image_name = \"gel_#{gel.id}\"\n            \n            # image gel\n            image_gel gel, image_name\n            \n            # upload image\n            ups = uploadData(\"#{DIRNAME}/#{image_name}\", 1, TRIES) # 1 file per gel\n            # associate to plan, op \n            up=nil\n            if(!(ups.nil?))\n                up=ups[0]\n                grouped_ops.first.plan.associate(image_name, {}, up)\n                gel.associate(image_name, {}, up)\n                grouped_ops.each do |op| # associate to all operations connected to gel\n                    op.associate(image_name, {}, up)\n                end\n                # can\'t associate to outputs yet because they are only made if lengths are verified\n            end\n            \n            # check lengths of fragments in gel\n            check_frag_length gel, grouped_ops\n            # grouped_ops.map { |op| op.temporary[:correct] = CORRECT.rotate!.first } if debug\n            \n            \n            # check whether fragment matched length\n            grouped_ops.each { |op|\n              if(op.temporary[:correct].upcase.start_with?(\"N\"))\n                op.error :incorrect_length, \"The fragment did not match the expected length.\"\n              end\n            }\n            \n            # get grouped_ops that have not errored\n            \n            grouped_ops.select! { |op| op.status == \"running\"  }\n\n            show { note \"Making the following ops: #{grouped_ops.map { |op| op.id }}\"}\n            grouped_ops.make    # contains only running ops from here on !!!\n            show { note \"#{grouped_ops.map { |op| op.output(FRAGMENT_OUT).item }}\" }\n\n            \n            if(grouped_ops.any?)\n              # cut fragments\n              cut_fragments grouped_ops  \n  \n              # weigh fragments\n              weigh_fragments grouped_ops\n             \n              # associate gel image, fragment lane with fragment and weight with the gel slices to output\n              grouped_ops.each { |op|\n                op.output(FRAGMENT_OUT).item.associate(image_name, \"Your fragment is in row #{op.input(FRAGMENT).row + 1} and column #{op.input(FRAGMENT).column + 1}\", up) \n                op.output(FRAGMENT_OUT).item.associate(:weight, op.temporary[:weight]) \n              }\n              \n            else \n              # do we want this? ask cami/sam\n              show {\n                  title \"Your lucky day!\"\n                  note \"No fragments to extract from gel #{gel}.\"\n              }\n            end # grouped_ops.any?\n            \n            # clean up after gel\n            clean_up gel, gels\n            \n            # delete collection\n            gel.mark_as_deleted\n            \n        } # gels.each\n        \n        ok_ops=operations.running\n        operations=ok_ops\n    \n        # are we cleaning from gel now?\n        choice = show {\n            title \"What Next?\"\n            select [\"Yes\", \"No\"], var: \"choice\", label: \"Would you like to purify the gel slices immediately?\"\n        }\n        plans = operations.map { |op| op.plan }.uniq\n        plans.each { |plan|\n            plan.associate :choice, choice[:choice]\n        }\n        if(choice[:choice] == \"Yes\")\n            show {\n              title \"Keep Gel Slices\"\n              note \"Keep the gel slices #{operations.map{ |op| op.output(FRAGMENT_OUT).item}.to_sentence} on your bench to use in the next protocol.\"\n            }\n        else\n            operations.store\n        end\n    \n        return {}\n    end\n    \n    def image_gel gel, image_name\n      show do\n        title \"Image gel #{gel}\"\n        check \"Clean the transilluminator with ethanol.\"\n        check \"Put the gel #{gel} on the transilluminator.\"\n        check \"Turn off the room lights before turning on the transilluminator.\"\n        check \"Put the camera hood on, turn on the transilluminator and take a picture using the camera control interface on computer.\"\n        check \"Check to see if the picture matches the gel before uploading.\"\n        check \"Rename the picture you just took exactly as <b>#{image_name}</b>.\"\n      end\n    end\n    \n    def check_frag_length gel, grouped_ops\n      show {\n        title \"Verify Fragment Lengths for gel #{gel}\"\n        table grouped_ops.start_table\n          .custom_column(heading: \"Gel ID\") { |op| op.input(FRAGMENT).item.id }\n          .custom_column(heading: \"Row\") { |op| op.input(FRAGMENT).row + 1 }\n          .custom_column(heading: \"Column\", checkable: true) { |op| op.input(FRAGMENT).column + 1 }\n          .custom_column(heading: \"Expected Length\") { |op| op.output(FRAGMENT_OUT).sample.properties[\"Length\"] }\n          .get(:correct, type: \'text\', heading: \"Does the band match the expected length? (y/n)\", default: \'y\')\n        .end_table\n      }\n    end   \n    \n    def cut_fragments grouped_ops  \n      show {\n        title \"Cut Out Fragments\"\n        note \"Take out #{grouped_ops.length} 1.5 mL tubes and label accordingly: #{grouped_ops.map { |op| \"#{op.output(\"Fragment\").item}\" }.to_sentence}\"\n        note \"Now, cut out the bands and place them into the 1.5 mL tubes according to the following table:\"\n        table grouped_ops.start_table \n          .custom_column(heading: \"Gel ID\") { |op| \"#{op.input(FRAGMENT).item}\" }\n          .custom_column(heading: \"Row\") { |op| op.input(FRAGMENT).row + 1 }\n          .custom_column(heading: \"Column\", checkable: true) { |op| op.input(FRAGMENT).column + 1 }\n          .custom_column(heading: \"1.5 mL Tube ID\") { |op| \"#{op.output(FRAGMENT_OUT).item}\" }\n          .custom_column(heading: \"Length\") { |op| op.output(FRAGMENT_OUT).sample.properties[\"Length\"] }\n        .end_table\n      }\n    end\n    \n    def weigh_fragments grouped_ops\n      show {\n        title \"Weigh Gel Slices\"\n        note \"Perform this step using the scale inside the gel room.\"\n        check \"Zero the scale with an empty 1.5 mL tube.\"\n        check \"Weigh each slice and enter the weights in the following table:\"\n        table grouped_ops.start_table\n          .custom_column(heading: \"1.5 mL Tube ID\") { |op| \"#{op.output(FRAGMENT_OUT).item}\" }\n          .get(:weight, type: \'number\', heading: \"Weight (g)\",  default: MIN_WEIGHT)\n          .end_table\n      }\n    end\n    \n    def clean_up gel, gels\n      show {\n        title \"Clean Up\"\n        check \"Turn off the transilluminator.\"\n        check \"Dispose of the gel #{gel} and any gel parts by placing it in the waste container. Spray the surface of the transilluminator with ethanol and wipe until dry using a paper towel.\"\n        check \"Clean up the gel box and casting tray by rinsing with water. Return them to the gel station.\"\n        if(gel==gels.last)\n            check \"Dispose gloves after leaving the room.\"\n        end\n      }\n    end\nend',4,'OperationType','2018-07-17 21:27:49','2018-07-17 21:27:49',1),(126,'protocol','needs \"Cloning/StandardCloning\"\nneeds \"Cloning/GradientPCR\"\n\nclass Protocol\n    \n  # I/O\n  FWD = \"Forward Primer\"\n  REV = \"Reverse Primer\"\n  TEMPLATE = \"Template\"\n  FRAGMENT = \"Fragment\"\n  \n  # other\n  SEC_PER_KB = 30 # sec, extension timer per KB for KAPA\n  \n  # get the gradient PCR magic\n  include StandardCloning\n  include GradientPCR\n\n  def main\n    if debug\n      operations.retrieve interactive: false\n      item = operations[0].input(FWD).item\n      operations.each do |op|\n        if rand(2) < 1\n          op.input(REV).set item: item\n          op.input(FWD).set item: item\n        end\n      end\n    end\n    # grab all necessary items\n    dilute_stocks_and_retrieve TEMPLATE\n    kapa_stock_item = find(:sample, name: \"Kapa HF Master Mix\")[0].in(\"Enzyme Stock\")[0]\n    take [kapa_stock_item], interactive: true,  method: \"boxes\"\n    \n    #check the volumes of input primers for all operations, and ensure they are sufficient\n    operations.each { |op| op.temporary[:primer_vol] = 2.5 }\n    check_volumes [FWD, REV], :primer_vol, :make_aliquots_from_stock, check_contam: true\n    \n    # build a pcrs hash that groups pcr by T Anneal\n    pcrs = build_pcrs_hash\n\n    # show the result of the binning algorithm\n    pcrs.each_with_index do |pcr, idx|\n      show { title \"pcr #{idx}\"}\n      log_bin_info pcr\n    end if debug\n\n    # generate a table for stripwells\n    stripwell_tab = build_stripwell_table pcrs\n    \n    # prepare and label stripwells for PCR\n    prepare_stripwells stripwell_tab\n    \n    # add templates to stripwells for pcr\n    load_templates pcrs\n    \n    # add primers to stripwells\n    load_primers pcrs\n\n    # add kapa master mix to stripwells\n    add_mix stripwell_tab, kapa_stock_item\n    \n    # run the thermocycler\n    start_pcr pcrs\n    \n    # store \n    operations.running.store io: \"input\", interactive: true, method: \"boxes\"\n    release [kapa_stock_item], interactive: true\n    \n    return {}\n  end\n  \n  # dilute to 1ng/uL stocks if necessary\n  def dilute_stocks_and_retrieve input\n  \n    # only use inputs that haven\'t been diluted and that don\'t have diluted stocks already\n    ops_w_undiluted_template = operations.reject { true }\n    operations.each do |op|\n        next if op.input(input).object_type.name.include?(\"1 ng/µL\")\n        \n        sample = op.input(input).sample\n        ot_name = op.input(input).object_type.name.include?(\"Unverified\") ? \"1 ng/µL Plasmid Stock\" : \"1 ng/µL \" + sample.sample_type.name + \" Stock\"\n        diluted_stock = sample.in(ot_name).first\n        \n        if diluted_stock\n            op.input(input).set item: diluted_stock\n        else\n            new_stock = produce new_sample sample.name, of: sample.sample_type.name, as: ot_name\n            op.temporary[:diluted_stock] = new_stock\n            \n            ops_w_undiluted_template.push op\n        end\n    end\n    \n    # retrieve operation inputs (doesn\'t include the stocks replaced by diluted stocks above)\n    ops_w_undiluted_template.retrieve\n    \n    # all stocks may be diluted already\n    if ops_w_undiluted_template.empty?\n        operations.retrieve\n        return\n    end\n    \n    # ensure concentrations\n    check_concentration ops_w_undiluted_template, input\n    \n    # dilute stocks\n    show do\n      title \"Make 1 ng/L Template Stocks\"\n      \n      check \"Grab #{ops_w_undiluted_template.length} 1.5 mL tubes, label them with #{ops_w_undiluted_template.map { |op| op.temporary[:diluted_stock].id }.join(\", \")}\"\n      check \"Add template stocks and water into newly labeled 1.5 mL tubes following the table below\"\n      \n      table ops_w_undiluted_template\n          .start_table\n          .custom_column(heading: \"Newly-labeled tube\") { |op| op.temporary[:diluted_stock].id }\n          .input_item(input, heading: \"Template stock, 1 L\", checkable: true)\n          .custom_column(heading: \"Water volume\", checkable: true) { |op| op.input(input).item.get(:concentration).to_f - 1 }\n          .end_table\n      check \"Vortex and then spin down for a few seconds\"\n    end\n    \n    # return input stocks\n    release ops_w_undiluted_template.map { |op| op.input(input).item }, interactive: true, method: \"boxes\"\n    \n    # retrieve the rest of the inputs\n    operations.reject { |op| ops_w_undiluted_template.include? op }.retrieve\n    \n    # set diluted stocks as inputs\n    ops_w_undiluted_template.each { |op| op.input(input).set item: op.temporary[:diluted_stock] }\n  end\n  \n  \n  # TODO dilute from stock if item is aliquot\n  # Callback for check_volume.\n  # takes in lists of all ops that have input aliquots with insufficient volume, sorted by item,\n  # and takes in the inputs which were checked for those ops.\n  # Deletes bad items and remakes each from primer stock\n  def make_aliquots_from_stock bad_ops_by_item, inputs\n    # bad_ops_by_item is accessible by bad_ops_by_item[item] = [op1, op2, op3...]\n    # where each op has a bad volume reading for the given item\n    \n    # Construct list of all stocks needed for making aliquots. Error ops for which no primer stock is available\n    # for every non-errored op that has low item volume,\n    # replace the old aliquot item with a new one. \n    aliquots_to_make = 0\n    stocks = []\n    ops_by_fresh_item = Hash.new(0)\n    stock_table = [[\"Primer Stock ID\", \"Primer Aliquot ID\"]]\n    transfer_table = [[\"Old Aliquot ID\", \"New Aliquot ID\"]]\n    bad_ops_by_item.each do |item, ops|\n      stock = item.sample.in(\"Primer Stock\").first ######## items is a string?\n      if stock.nil?\n        ops.each { |op| op.error :no_primer, \"You need to order a primer stock for primer sample #{item.sample.id}.\" }\n        bad_ops_by_item.except! item\n      else\n        stocks.push stock\n        aliquots_to_make += 1\n        item.mark_as_deleted\n        fresh_item = produce new_sample item.sample.name, of: item.sample.sample_type.name, as: item.object_type.name\n        bad_ops_by_item.except! item\n        ops_by_fresh_item[fresh_item] = ops\n        ops.each do |op| \n          input = inputs.find { |input| op.input(input).item == item }\n          op.input(input).set item: fresh_item\n        end\n        stock_table.push [stock.id, {content: fresh_item.id, check: true}]\n        if item.get(:contaminated) != \"Yes\"\n          transfer_table.push [item.id, {content: fresh_item.id, check: true}]    \n        end\n      end\n    end\n    \n    bad_ops_by_item.merge! ops_by_fresh_item\n    take stocks, interactive: true\n    \n    # label new aliquot tubes and dilute\n    show do \n      title \"Grab 1.5 mL tubes\"\n      \n      note \"Grab #{aliquots_to_make} 1.5 mL tubes\"\n      note \"Label each tube with the following ids: #{bad_ops_by_item.keys.map { |item| item.id }.sort.to_sentence}\"\n      note \"Using the 100 uL pipette, pipette 90uL of water into each tube\"\n    end\n  \n    # make new aliquots\n    show do \n      title \"Transfer primer stock into primer aliquot\"\n      \n      note \"Pipette 10 uL of the primer stock into the primer aliquot according to the following table:\"\n      table stock_table\n    end\n    \n    \n    if transfer_table.length > 1\n      show do\n        title \"Transfer Residual Primer\"\n        \n        note \"Transfer primer residue from the low volume aliquots into the fresh aliquots according to the following table:\"\n        table transfer_table\n      end\n    end\n    \n    release stocks, interactive: true\n  end\n  \n  # build a pcrs hash that groups pcr by T Anneal\n  def build_pcrs_hash\n    pcrs = distribute_pcrs operations.running, 4\n    pcrs.each do |pcr|\n      lengths = pcr[:ops_by_bin].values.flatten.collect { |op| op.output(FRAGMENT).sample.properties[\"Length\"] }\n      extension_time = (lengths.max)/1000.0*SEC_PER_KB\n      # adding more extension time for longer size PCR.\n      if lengths.max < 2000\n        extension_time += 30\n      elsif lengths.max < 3000\n        extension_time += 60\n      else\n        extension_time += 90\n      end\n      extension_time = 3 * 60 if extension_time < 3 * 60\n      pcr[:mm], pcr[:ss] = (extension_time.to_i).divmod(60)\n      pcr[:mm] = \"0#{pcr[:mm]}\" if pcr[:mm].between?(0, 9)\n      pcr[:ss] = \"0#{pcr[:ss]}\" if pcr[:ss].between?(0, 9)\n\n      # set up stripwells (one for each temperature bin)\n      pcr[:ops_by_bin].each do |bin, ops|\n          ops.make\n          pcr[:stripwells] += ops.output_collections[FRAGMENT]\n      end\n    end\n    pcrs\n  end\n  \n  # generate a table for stripwells\n  def build_stripwell_table pcrs\n    stripwells = pcrs.collect { |pcr| pcr[:stripwells] }.flatten\n    stripwell_tab = [[\"Stripwell\", \"Wells to pipette\"]] + stripwells.map { |sw| [\"#{sw.id} (#{sw.num_samples <= 6 ? 6 : 12} wells)\", { content: sw.non_empty_string, check: true }] }\n  end\n  \n  # prepare and label stripwells for PCR\n    def prepare_stripwells stripwell_tab\n    show do\n      title \"Label and prepare stripwells\"\n      \n      note \"Label stripwells, and pipette 19 L of molecular grade water into each based on the following table:\"\n      table stripwell_tab\n      stripwell_tab\n    end\n  end\n  \n  # add templates to stripwells for pcr\n  def load_templates pcrs\n    pcrs.each_with_index do |pcr, idx|\n      show do\n        title \"Load templates for PCR ##{idx + 1}\"\n        \n        pcr[:ops_by_bin].each do |bin, ops|\n          table ops\n              .start_table\n              .output_collection(FRAGMENT, heading: \"Stripwell\")\n              .custom_column(heading: \"Well\") { |op| op.output(FRAGMENT).column + 1 }\n              .input_item(TEMPLATE, heading: \"Template, 1 L\", checkable: true)\n              