l: \"Location\", default: \"Bench\"\n        end\n         \n        object_type = ObjectType.find_by_name(item_info[:object_type])\n        new_item = produce new_collection object_type.name\n        new_item.matrix = Array.new(object_type.rows) { Array.new(object_type.columns) { -1 } }\n        new_item.save\n        \n        op.output(\"Batch\").set item: new_item\n    \n        show do\n            title \"Collection Created!\"\n            \n            note \"Made new #{op.output(\"Batch\").item.object_type.name}\"\n            note \"Collection link #{op.output(\"Batch\").item}\"\n            note \"Now we will populate the collection with samples of your choice\"\n        end\n        \n        \n        coll = op.output(\"Batch\").collection\n        coll.location = item_info[:location]\n        continue = true\n        while continue\n            add = show do \n                title \"how many samples to add to #{coll.id}\"\n                note \"Make sure to specify sample name exactly correct\"\n                \n                get \"number\", var: \"sample\", label: \"Sample to add\", default: \"Gibson Aliquot\"\n                get \"number\", var: \"add\", label: \"how many to add\", default: 0\n                select [\"Yes\", \"No\"], var: \"continue\", label: \"I want to add a different sample as well\", default: 1\n                note \"Below is a representation of the collection state. -1 means no sample in that slot.\"\n                table coll.matrix\n            end        \n            \n            sample = Sample.find_by_name(add[:sample])\n            \n            add[:add].times do\n                coll.add_one sample\n            end\n            continue = add[:continue] == \"Yes\"\n        end\n        \n        show do \n            title \"Collection finsished and ready to use\"\n            \n            note \"Use the \\\'edit collection\\\' protocol to add or remove samples\"\n            table coll.matrix\n        end\n    end\n    return {}\n  end\n\nend\n',49,'OperationType','2018-07-19 22:30:58','2018-07-19 22:30:58',1),(338,'protocol','needs \"Cloning/StandardCloning\"\n\nclass Protocol\n    \n  include StandardCloning\n\n  def main\n    operations.retrieve(interactive: false)\n    \n    # Increase the number of colonies picked the plate. If no picked number is present,\n    # set it equal to one. Note that setting the status to \"error\" will remove the operation\n    # from operations.running, so it will not be listed in tables, etc.\n    operations.select { |op| op.input(\"Plasmid\").item.object_type_id == ObjectType.where(name: \"Checked E coli Plate of Plasmid\").first.id }.each do |op|\n       nc = (op.input_data \"Plasmid\", :num_colonies).to_i\n       np = (op.input_data \"Plasmid\", :num_picked).to_i\n       if debug && !nc && rand(2) == 1\n         op.set_input_data \"Plasmid\", :num_colonies, 1\n         op.set_input_data \"Plasmid\", :num_picked, 1\n       elsif !debug && (!nc || nc == 0 || ( np && np >= nc ))\n         op.error :missing_data, \"No colonies left on plate or colony number not defined\"\n       else\n         op.set_input_data \"Plasmid\", :num_picked, (np || 0) + 1\n       end\n    end\n    \n    # Error out operations whose samples don\'t have bacterial marker data. Tell technician\n    # which ones are not being used. Quit if there are no samples left.\n    operations.each do |op|\n      unless op.input(\"Plasmid\").child_sample.properties[\"Bacterial Marker\"]\n        if debug && rand(2) == 1\n          op.input(\"Plasmid\").child_sample.set_property \"Bacterial Marker\", \"Amp\"\n        else\n          op.set_status \"error\"\n          op.associate :missing_marker, \"No bacterial marker associated with plasmid\"\n        end\n      end\n    end\n    \n    operations.make\n    \n    p_ot = ObjectType.where(name: \"Checked E coli Plate of Plasmid\").first \n    \n    raise \"Could not find object type \'Checked E coli Plate of Plasmid\'\" unless 