o_table \'output\'\n\n    show do\n      title \'Input Table\'\n      table tin.all.render\n    end\n\n    show do\n      title \'Output Table\'\n      table tout.all.render\n    end\n\n    operations.store\n\n    {}\n\n  end\n\nend\n',54,'OperationType','2018-08-16 17:53:15','2018-08-16 17:53:15',1),(370,'precondition','def precondition(_op)\n  true\nend',54,'OperationType','2018-08-16 17:53:15','2018-08-16 17:53:15',1),(371,'cost_model','def cost(_op)\n  { labor: 0, materials: 0 }\nend',54,'OperationType','2018-08-16 17:53:15','2018-08-16 17:53:15',1),(372,'documentation','Documentation here. Start with a paragraph, not a heading or title, as in most views, the title will be supplied by the view.',54,'OperationType','2018-08-16 17:53:15','2018-08-16 17:53:15',1),(373,'protocol','\n\n# This is a default, one-size-fits all protocol that shows how you can\n# access the inputs and outputs of the operations associated with a job.\n# Add specific instructions for this protocol!\n\nclass Protocol\n\n  def main\n\n    operations.retrieve.make\n\n    tin  = operations.io_table \'input\'\n    tout = operations.io_table \'output\'\n\n    show do\n      title \'Input Table\'\n      table tin.all.render\n    end\n\n    show do\n      title \'Output Table\'\n      table tout.all.render\n    end\n\n    operations.store\n\n    {}\n\n  end\n\nend\n',55,'OperationType','2018-08-16 18:01:17','2018-08-16 18:01:17',1),(374,'precondition','def precondition(_op)\n  true\nend',55,'OperationType','2018-08-16 18:01:17','2018-08-16 18:01:17',1),(375,'cost_model','def cost(_op)\n  { labor: 0, materials: 0 }\nend',55,'OperationType','2018-08-16 18:01:17','2018-08-16 18:01:17',1),(376,'documentation','Documentation here. Start with a paragraph, not a heading or title, as in most views, the title will be supplied by the view.',55,'OperationType','2018-08-16 18:01:17','2018-08-16 18:01:17',1),(377,'protocol','\n\n# This is a default, one-size-fits all protocol that shows how you can\n# access the inputs and outputs of the operations associated with a job.\n# Add specific instructions for this protocol!\n\nclass Protocol\n\n  def main\n\n    operations.retrieve.make\n\n    tin  = operations.io_table \'input\'\n    tout = operations.io_table \'output\'\n\n    show do\n      title \'Input Table\'\n      table tin.all.render\n    end\n\n    show do\n      title \'Output Table\'\n      table tout.all.render\n    end\n\n    operations.store\n\n    {}\n\n  end\n\nend\n',56,'OperationType','2018-08-16 18:01:28','2018-08-16 18:01:28',1),(378,'precondition','def precondition(_op)\n  true\nend',56,'OperationType','2018-08-16 18:01:28','2018-08-16 18:01:28',1),(379,'cost_model','def cost(_op)\n  { labor: 0, materials: 0 }\nend',56,'OperationType','2018-08-16 18:01:28','2018-08-16 18:01:28',1),(380,'documentation','Documentation here. Start with a paragraph, not a heading or title, as in most views, the title will be supplied by the view.',56,'OperationType','2018-08-16 18:01:28','2018-08-16 18:01:28',1),(381,'protocol','#Abe/Garrett 8-12-2017\n#Edited by Garrett 8-13-17\n#edited by ayesha 5-3-18 -- goodness, gracious, me, oh my; time seems to positively fly\n\nneeds \"Cloning Libs/Cloning\"\n\nclass Protocol\n\n    include Cloning\n      \n      QUANTITY_OF_DNA = 4000 #in ng\n      VOL_OF_ENZYME = 1 #in uL\n      VOL_OF_BUFFER = 4 #in uL\n      TOTAL_VOL_RXN = 40 #in uL\n      INCUBATION_TEMP = \"37 C\"\n      INCUBATION_TIME = 60 #min \n      MIN_CONC = 333 #in ng/uL\n\n    def main\n      buffer = Sample.find_by_name(\"Cut Smart\").in(\"Enzyme Buffer Stock\").first\n      take [buffer], interactive: true, method: \"boxes\"\n      operations.retrieve only: [\"Plasmid\"]\n      check_concentration operations, \"Plasmid\"\n      debug = true\n      if debug \n        operations.each do |op| \n          op.input(\"Plasmid\").item.associate :concentration, 400\n        end\n      end\n\n      operations.each do |op|\n          stock = op.input(\"Plasmid\").item\n          enzymes = op.input_array(\"Enzymes\").items\n          # op.temporary[:enzyme_ids] = []\n          # op.input_array(\"Enzymes\").items.each  { |e| op.temporary[:enzyme_ids].push e.id}\n          # NOTE FOR FUTURE REFERENCE: :enzyme_ids stuff could be written more simply:\n          #   op.temporary[:enzyme_ids] = op.input_array(\"Enzymes\").items.map { |e| e.id }\n          if stock.get(:concentration) && stock.get(:concentration).to_f > 0 && stock.get(:concentration).to_f > 333\n            stock_vol = QUANTITY_OF_DNA / stock.get(:concentration).to_f.round(1)\n            op.temporary[:stock_vol] = stock_vol\n            op.temporary[:water_vol] = [(TOTAL_VOL_RXN - stock_vol - VOL_OF_BUFFER - VOL_OF_ENZYME * enzymes.length).to_f.round(1), 0].max\n          else\n            op.error :concenration, \"The plasmid stock either has too low a concentration or doesn\'t have a concentration associated with it.\"\n          end\n      end      \n        \n      if !operations.running.blank?  \n        # get ice block\n        show do\n            title \"Keep enzymes on ice block\"\n              \n            warning \"Grab an ice block, and place the enzymes on it while performing the following step!\"\n        end\n          \n        operations.retrieve only: [\"Enzymes\"]\n        operations.make\n        \n        # get stripwells\n        show do\n            title \"Grab stripwell(s) for restriction digest\"\n            \n            operations.output_collections[\"Digest\"].each do |sw|\n                check \"Grab a stripwell with #{sw.num_samples} wells, and label it #{sw.id}.\"\n            end\n        end\n          \n        # calculate volumes and build tmp array of enzyme ids\n        \n        # old calculations (for posterity)\n        # templates_with_volume = templates.map.with_index { |t, idx| \"#{template_vols[idx]} L of #{t.id}\" }\n        # buffer_with_volume = templates.map { |t| \"1 L of #{buffer.id}\" }\n        # enzymes_with_volume = enzymes.map { |es| \"0.5 L#{es.length > 1 ? \" each\" : \"\"} of #{es.map { |e| \"#{e.id}\" }.join(\" and \") }\" }\n        # water_with_volume = water_vols.map { |wv| \"#{wv} L\" }\n        \n        # add water to wells\n        show do\n            title \"Load Stripwell with Molecular Grade Water\"\n            \n            table operations.start_table\n                .output_collection(\"Digest\", heading: \"Stripwell\")\n                .custom_column(heading: \"Well\") { |op| op.output(\"Digest\").column + 1 } # this gives us wells that start at 1, rather than 0, like output_column gives us\n                .custom_column(heading: \"Molecular Grade Water (uL)\", checkable: true) { |op| op.temporary[:water_vol].to_f.round(1) }\n            .end_table\n            warn = check_p2 operations, :water_vol\n            note \"#{warn}\"\n        end\n        \n        # add smart buffer to wells\n        # show do\n        #     title \"Load Stripwell with Cut Smart Buffer\"\n        #     operations.each do |op|\n        #         check \"Add 1L of Buffer to well #{op.output(\"Digest\").column} of Stripwell #{op.output(\"Digest\").collection.id}.\"\n        #     end\n        # end\n        # I think this version cuts down on redundancy\n        show do\n            title \"Load Stripwell with Cut Smart Buffer\"\n            \n            note \"Add #{VOL_OF_BUFFER}uL of Buffer to the following stripwell(s):\"\n            operations.output_collections[\"Digest\"].each do |sw|\n                check \"Stripwell #{sw.id}, wells 1-#{sw.num_samples}\"\n            end\n        end\n        \n        # add enzymes to wells\n        show do\n            title \"Load Stripwell with Enzymes\"\n            \n            note \"Load wells with #{VOL_OF_ENZYME} uL of each specified enzyme\"\n            \n            table operations.start_table\n                .output_collection(\"Digest\", heading: \"Stripwell\")\n                .custom_column(heading: \"Well\") { |op| op.output(\"Digest\").column + 1 }\n                .custom_column(heading: \"Enzymes (add #{VOL_OF_ENZYME} uL of each)\", checkable: true) { |op| op.input_array(\"Enzymes\").items.map { |e| e.id }.to_sentence } # .to_sentence is neat!\n            .end_table\n        end\n        \n        # add plasmid stock to wells\n        show do\n          title \"Load Stripwell with Template\"\n            \n          table operations.start_table\n              .output_collection(\"Digest\", heading: \"Stripwell\")\n              .custom_column(heading: \"Well\") { |op| op.output(\"Digest\").column + 1 }\n              .input_item(\"Plasmid\")\n              .custom_column(heading: \"Volume to add (uL)\", checkable: true) { |op| op.temporary[:stock_vol].to_f.round(1) }\n          .end_table\n          warn = check_p2 operations, :stock_vol \n        warning \"#{warn}\"\n        end\n        \n        # put away ingredients, and place output stripwells in incubator\n        operations.output_collections[\"Digest\"].each { |c| c.move \"#{INCUBATION_TEMP} standing incubator\"}\n        operations.store io: \"input\", interactive: true, method: \"boxes\"\n        release [buffer], interactive: true, method: \"boxes\"\n        release operations.output_collections[\"Digest\"], interactive: true\n        \n        # start incubation timer\n        show do\n          title \"Start incubation timer\"\n          \n          check \"<a href=\'https://www.google.com/search?q=one+hour+timer&oq=#{INCUBATION_TIME}+minute+timer&aqs=chrome..69i57j69i60j69i65l2j69i60l2.1258j0j9&sourceid=chrome&ie=UTF-8\' target=\'_blank\'>Click here</a> to start a #{INCUBATION_TIME} minute timer.\"\n          note \"After timer finishes, go on to the next steps.\"\n        end\n        \n        #grab stripwells back from incubator, and transfer to fridge\n        take operations.output_collections[\"Digest\"], interactive: true\n        operations.output_collections[\"Digest\"].each { |c| c.move  \"R4 (beneath the DI dispensers)\" }\n        operations.store io: \"output\", interactive: true\n      end  \n\n      return {}\n    end    \n    # displays warning to use P2 pipette if necessary, for use in adding water and plasmid stock\n    def check_p2 operations, vol\n      operations.each do |op| \n          if op.temporary[vol] < 0.4\n              return \"There are volumes smaller than 0.4; please use the P2!\"\n          end\n      end\n      \"\"\n    end\nend',56,'OperationType','2018-08-16 18:01:40','2018-08-16 18:01:40',1),(382,'cost_model','def cost(_op)\n  { labor: 12.6, materials: 0.69 }\nend',56,'OperationType','2018-08-16 18:07:29','2018-08-16 18:07:29',1),(383,'documentation','Input a Plasmid Stock and an array of Enzymes to produce a digested plasmid that can currently be used for producing a Fragment Stock through our gel protocols.',56,'OperationType','2018-08-16 18:07:37','2018-08-16 18:07:37',1),(384,'documentation','Input a Plasmid Stock and an array of Enzymes to produce a digested plasmid that can currently be used for producing a Fragment Stock through gel protocols.',56,'OperationType','2018-08-16 18:07:41','2018-08-16 18:07:41',1),(385,'protocol','# Copied from rehydrate primer\n\nclass Protocol\n  INPUT = \"Lyophilized Fragment\"\n  STOCK = \"Fragment Stock\"\n  DILUTED_STOCK = \"Diluted Fragment\"\n\n  STOCK_CONCENTRATION = 20\n  \n  # This method tells the technician to pick up fragments from the EE office.\n  def get_fragments\n    show do\n      title \"Go the EE office to pick up fragments\"\n\n      note \"Abort this protocol if no primer has shown up. It will automatically rescheduled.\"\n    end\n  end\n  \n  # This method tells the technician to quick spin down all fragment tubes.\n  def quick_spin_fragment_tubes\n    show do\n      title \"Quick spin down all the fragment tubes\"\n\n      note \"Find the order with sales order (or supplier ref) number #{operations.first.input_data(\"Primer\", :order_number)}\"\n      note \"Put all the fragment tubes in a table top centrifuge to spin down for 3 seconds.\"\n\n      warning \"Make sure to balance!\"\n    end\n  end\n  \n  # This method tells the technician to label and rehydrate fragments.\n  def label_and_rehydrate\n    show do\n      title \"Label and rehydrate\"\n\n      note \"Label each fragment tube with the ids shown in Primer Stock ids and rehydrate with volume of TE shown in Rehydrate\"\n\n      table operations.start_table\n        .input_sample(INPUT)\n        .custom_column(heading: \'Names of inputs\') {|op| op.input(\"Lyophilized Fragment\").sample.name}\n        .output_item(STOCK)\n        .custom_column(heading: \"Rehydrate (uL of TE)\", checkable: true) {|op| op.temporary[:water_vol]}\n      .end_table\n    end\n  end\n  \n  # This method tells the technician to vortex and centrifuge fragments.\n  def vortex_and_centrifuge\n    show do\n      title \"Vortex and centrifuge\"\n\n      note \"Wait one minute for the fragment to dissolve in TE.\" if operations.length < 7\n      note \"Vortex each tube on table top vortexer for 5 seconds and then quick spin for 2 seconds on table top centrifuge.\"\n    end\n  end\n  \n  # This method tells the technician to grab tubes and label them.\n  def grab_tubes\n    show do\n      title \"Grab #{operations.running.length} 1.5 mL tubes\"\n\n      check \"Grab #{operations.running.length} 1.5 mL tubes, label with following ids: #{operations.map {|op| \"#{op.output(DILUTED_STOCK).item.id}\"}.join(\", \")}\"\n      check \"Add water according to table.\"\n      table operations.running.start_table\n        .output_item(STOCK)\n        .custom_column(heading: \"H20 (uL)\") {|op| op.temporary[:dilution_water_vol]}\n      .end_table\n    end\n  end\n  \n  # This method tells the technician to make primer aliquots.\n  def make_primer_aliquots\n    show do\n      title \"Make primer aliquots\"\n\n      note \"Pipette #{operations.running.first.temporary[:dilution_vol] } uL #{STOCK} into each #{DILUTED_STOCK} tube using the following table.\"\n\n      table operations.running.start_table\n        .output_item(STOCK, heading: \"#{STOCK} (#{operations.running.first.temporary[:dilution_vol]} uL)\")\n        .output_item(DILUTED_STOCK, checkable: true)\n      .end_table\n    end\n  end  \n  \n  def main\n\n    operations.retrieve interactive: false\n    operations.make\n\n    if debug\n      operations.running.each do |op|\n        op.set_input_data(INPUT, :ng, rand(250..1000))\n      end\n    end\n    \n    # Get fragment tubes from EE office\n    get_fragments\n    \n    # Quick spin down all the fragment tubes\n    quick_spin_fragment_tubes\n\n\n    operations.running.each do |op|\n      op.temporary[:conc] = STOCK_CONCENTRATION\n      show do\n          note \"op.input_data(INPUT, :ng): #{op.input_data(INPUT, :ng)}\"\n      end\n      op.temporary[:water_vol] = op.input_data(INPUT, :ng) / op.temporary[:conc]\n      op.temporary[:dilution_vol] = 1.0\n      op.temporary[:dilution_water_vol] = op.temporary[:dilution_vol] * op.temporary[:conc] - op.temporary[:dilution_vol]\n    end\n    \n    # label and rehydrate fragments\n    label_and_rehydrate\n\n    # Vortex and centrifuge\n    vortex_and_centrifuge\n\n    # grab 1.5mL tubes\n    grab_tubes\n    \n    # Make primer aliquots\n    make_primer_aliquots\n\n\n    operations.running.each do |op|\n      stock = op.output(STOCK).item\n      diluted_stock = op.output(DILUTED_STOCK).item\n      stock.associate :concentration, op.temporary[:conc]\n      stock.associate :volume, op.temporary[:water_vol] - op.temporary[:dilution_vol]\n      diluted_stock.associate :concentration, 1.0\n      diluted_stock.associate :volume, op.temporary[:dilution_water_vol]\n    end\n\n    operations.running.each {|op| op.input(INPUT).item.mark_as_deleted}\n\n    operations.store\n\n    return {}\n\n  end\n\nend\n',55,'OperationType','2018-08-16 18:09:08','2018-08-16 18:09:08',1),(386,'protocol','class Protocol\n\n  def main\n    operations.retrieve interactive: false\n    operations.make\n    \n    # grab primers from EE office\n    pick_up_primers\n    \n    # Centrifuge ordered primer\n    spin_down_primers\n    \n    # Get nMoles of primer for each tube\n    get_primer_nm\n    \n    # Label primers and add TE\n    rehydrate_primers\n    \n    # Prepare tubes to make aliquots with\n    prepare_aliquot_tubes\n    \n    # Finish preparing primer stock tubes\n    vortex_and_centrifuge\n    \n    # Add primer stock to prepared aliquot tubes\n    make_aliquots\n    \n    operations.each { |op| op.input(\"Primer\").item.mark_as_deleted }\n    operations.store\n    \n    return {}\n  end\n\n  # This method tells the technician to pick up primers for this protocol.\n  def pick_up_primers\n    show do\n      title \"Go the EE office to pick up primers\"\n      \n      note \"Abort this protocol if no primer has shown up. It will automatically rescheduled.\"\n    end\n  end\n  \n  # This method tells the technician to spin down the primers\n  def spin_down_primers\n    show do\n      title \"Quick spin down all the primer tubes\"\n      \n      note \"Find the order with sales order (or supplier ref) number #{operations.first.input_data(\"Primer\", :order_number)}\"\n      note \"Put all the primer tubes in a table top centrifuge to spin down for 3 seconds.\"\n      warning \"Make sure to balance!\"\n    end    \n  end\n  \n  # Queries the tech for the nMoles of primer as written on tube and stores the measurement in operation.temporary[:n_moles]\n  def get_primer_nm\n    show do\n      title \"Enter the nMoles of the primer\"\n      \n      note \"Enter the number of moles for each primer, in nm. This is written toward the bottom of the tube, below the MW.\"\n      note \"The id of the primer is listed before the primer\'s name on the side of the tube.\"\n      table operations\n          .start_table\n          .input_sample(\"Primer\")\n          .get(:n_moles, type: \"number\", heading: \"nMoles\", default: 10)\n          .end_table\n    end    \n  end\n  \n  # label the primer tubes with their new unique id, and rehydrate each with an amount of TE dependent on the nMoles of primer.\n  def rehydrate_primers\n    show do\n      title \"Label and rehydrate\"\n      \n      note \"Label each primer tube with the ids shown in Primer Stock ids and rehydrate with volume of TE shown in Rehydrate\"\n      table operations\n          .start_table\n          .input_sample(\"Primer\")\n          .output_item(\"Primer Stock\")\n          .custom_column(heading: \"Rehydrate (uL of TE)\", checkable: true) { |op| op.temporary[:n_moles] * 10 }\n          .end_table\n    end    \n  end\n  \n  # Tells the technician to vortex and centrifuge \n  def vortex_and_centrifuge\n    show do\n      title \"Vortex and centrifuge\"\n      \n      note \"Wait one minute for the primer to dissolve in TE.\" if operations.length < 7\n      note \"Vortex each tube on table top vortexer for 5 seconds and then quick spin for 2 seconds on table top centrifuge.\"\n    end    \n  end\n  \n  # Prepare the aliquot tubes\n  def prepare_aliquot_tubes\n    show do\n      title \"Grab #{operations.length} 1.5 mL tubes\"\n      \n      check \"Grab #{operations.length} 1.5 mL tubes, label with following ids: #{operations.map { |op| \"#{op.output(\"Primer Aliquot\").item.id}\" }.join(\", \")}\"\n      check \"Add 90 uL of water into each above tube.\"\n    end    \n  end\n\n  # Displays a table and make primer aliquots \n  def make_aliquots\n    show do\n      title \"Make primer aliquots\"\n      \n      note \"Add 10 uL from each primer stock into each primer aliquot tube using the following table.\"\n      \n      table operations\n          .start_table\n          .output_item(\"Primer Stock\", heading: \"Primer Stock (10 uL)\")\n          .output_item(\"Primer Aliquot\", checkable: true)\n          .end_table\n      note \"Vortex each tube after the primer has been added.\"\n      \n    end    \n  end\nend\n',